Window gui::build(){Window w;w.setBackgroundColor({66,33,99,255});w.setWidth(800);w.setHeight(600);w.setX(100);w.setY(100);w.setFontSize(24);w.setTitle("Hello World");w.setFullscreen(0);w.setResizable(1);Label l1;l1.setText("Hello World");l1.setColor({255,255,255,255});l1.setBackgroundColor({66,33,99,0});l1.setPadding(0);l1.setAlignment(RIGHT);l1.setHoverColor({255,255,255,255});l1.setHoverBackgroundColor({66,33,99,0});l1.setId("Label1");l1.setX(0);l1.setY(0);l1.setZIndex(0);l1.setWidth(200);l1.setHeight(50);TextInput t1;t1.setPlaceholder("text input");t1.setColor({255,255,255,255});t1.setBackgroundColor({66,33,99,0});t1.setPadding(10);t1.setAlignment(RIGHT);t1.setHoverColor({255,255,255,255});t1.setHoverBackgroundColor({66,33,99,0});t1.setId("TextInput1");t1.setX(100);t1.setY(100);t1.setZIndex(1);t1.setWidth(200);t1.setHeight(50);t1.setCursorColor({255,255,255,255});return w;}